import { createGlobalStyle } from 'styled-components';



export const GlobalStyles = createGlobalStyle`

  :root{

    font-size: 62.5%;

  }


  *{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Montserrat', sans-serif;
  }


  body,
  html{

    background: #F8F8F2;
  }

`;
