import styled from 'styled-components';



export const Text = styled.h1`
  text-align: center;
  font-size: 5rem;
  color: #282A36;  
`;
export const StyledMain = styled.h1`
  display: flex;
  align-items: center;
  justify-content: center;
  background: #F8F8F2;
  height: calc(100vh - 8rem);
`;
export const StyledButton = styled.h1`
  color: red;
  text-align: center;
`;
