import styled from 'styled-components';



export const Text = styled.h1`
  text-align: center;
  font-size: 8rem;
  font-weight: bold;
  color: #282A36;  
`;
export const StyledMain = styled.h1`
  display: flex;
  align-items: center;
  justify-content: center;
  background: #F8F8F2;
  height: calc(100vh - 8rem);
`;
export const StyledButton = styled.button`
  width: 10rem;
  padding: 2rem 4rem;
  color: red;
  text-align: center;
  margin: 0 auto;
`;
