import styled, { css } from 'styled-components';


export const StyledCard = styled.article`
  display: flex;

  background: #44475A;
  width: 50%;


`


export const Text = styled.h1`

margin: 2rem 0 0 2rem;

${props => props.title && css`

  margin: 0.5rem 0 0  2rem;
  color: #F8F8F2;
  font-weight: bold;

`}
${props => props.description && css`


  margin: 0.5rem 0 0  2rem;
  color: #CCCCCC;
`}




`
